    handleInputChange = e => {
        this.setState({ newTweet: e.target.value });
    }

    handleNewTweet = e => {
        if (e.keycode != 13) return;

        const content = this.state.newTweet;
        const author = localStorage.getItem("@GoTwitter:username");
